let Orders,Users;_fb2‍.x([["handle_request",()=>handle_request]]);_fb2‍.w("../../models/order",[["default",["Orders"],function(v){Orders=v}]]);_fb2‍.w("../../models/user",[["default",["Users"],function(v){Users=v}]]);


const handle_request = (order_id, callback) => {
    return Orders.findById(order_id)
        .populate('dishes.dish_id')
        .lean()
        .then(order => {
            const dishes = order.dishes.map(dish => ({
                _id: dish.dish_id._id,
                name: dish.dish_id.name,
                quantity: dish.quantity,
                amount: dish.dish_id.amount
            }));
            return Users.findOne({
                orders: order_id
            }).then(user => {
                if (!callback) {
                    return {
                        _id: order._id,
                        buyer: {
                            name: user.first_name + " " + user.last_name,
                            address: user.address
                        },
                        dishes,
                        status: order.status,
                        amount: order.amount,
                        buyer_messages: order.buyer_messages,
                        vendor_messages: order.vendor_messages
                    }
                }
                callback(null, {
                    _id: order._id,
                    buyer: {
                        name: user.first_name + " " + user.last_name,
                        address: user.address
                    },
                    dishes,
                    status: order.status,
                    amount: order.amount,
                    buyer_messages: order.buyer_messages,
                    vendor_messages: order.vendor_messages
                })
            })
        })
};




